
public class Operators {

		public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int i = 5;
		int j = 2;
		double quotent = ((double)i)/j; // casting
		System.out.println(quotent);
		
		
		int l = i % j;
		System.out.println(l); 
		
		int x = 3001;
		System.out.println(x % 8 + 1);
	}

}
